import java.util.Scanner;

public class Main {

	public static void main(String[] args) {
		
		String codigoCompleto = "";

		Scanner input = new Scanner(System.in);
		
		int n = input.nextInt();
		
		while(n != 0){
			System.out.println(codigoCompleto.substring(0, n));
			n = input.nextInt();
		}
		
		
		
		input.close();
		
		
	}

}
